import styled from "styled-components";

export const AddTask = styled.div`
    color: #fff;
    margin-top: 20px;

    input {
        color: #fff;
        padding: 20px;
        outline: 0;
        border: 0;
        width: 95%;
        background-color: transparent;
    }
`

export const AddTaskArea = styled.div`
    display: flex;
    width: 100%;
    border-radius: 10px;
    align-items: center;
    border: 2px solid #797A81;
`
