import * as C from './AppStyles.js'
import { Global } from './styles/Global.js'
import { Header } from './components/Header/Header.js'
import { AddTask } from './components/AddTask/AddTask.js'
import { Content } from './components/Content/Content.js'
import { useEffect, useState } from 'react'
import { Search } from './components/Search/Search.js'

const App = () => {

  const API_URL = 'http://localhost:3500/items'

  const [items, setItems] = useState(JSON.parse(localStorage.getItem('listitems')))
  const [newitem, setNewItem] = useState('')
  const [search, setSearch] = useState('')
  const [fetchError, setFetchError] = useState(null)

  useEffect(() => {
    getItems()
  }, [])

  const getItems = async () => {
    try {
      const response = await fetch(API_URL)
      if(!response.ok) throw Error('Requisição Negada!')
      const json = response.json()
      setItems(json)
      setFetchError(null)
    } catch(e) {
      setFetchError(e.message)
    }
  }

  const handleChecked = (id) => {
    const listItems = items.map((item) => item.id === id ? { ...item, checked: !item.checked } : item)
    setItems(listItems)
    localStorage.setItem('listitems', JSON.stringify(listItems))
  }

  const handleDelete = (id) => {
    const listItems = items.filter((item) => item.id !== id)
    setItems(listItems)
    localStorage.setItem('listitems', JSON.stringify(listItems))
  }

  const handleAddTask = (item) => {
    let listItems = [...items];
    listItems.push({
      id: items.length + 1,
      checked: false,
      item: item
    });
    setItems(listItems);
    localStorage.setItem('listitems', JSON.stringify(listItems))
  }

  return (
    <>
      <C.Container>
        <main>
          <Header title={'Lista de Tarefas'} />
          <Search
            search={search}
            setSearch={setSearch}
          />
          <AddTask
            newitem={newitem}
            setNewItem={setNewItem}
            handleAddTask={handleAddTask}
          />

          {fetchError &&
            <p style={{color : 'red', marginTop : 20, textAlign : 'center'}}>{fetchError}</p>
          }

          {items.length > 0 &&
            <>
              <Content
                items={items.filter((item) => ((item.item).toLowerCase()).includes(search.toLowerCase()))}
                handleChecked={handleChecked}
                handleDelete={handleDelete}
              />
            </>
          }

          {items.length <= 0 &&
            <C.EmptyList>Sua lista está vazia</C.EmptyList>
          }
        </main>
      </C.Container>
      <Global />
    </>
  )
}

export default App
